// import components
import React, { Component } from "react";
import Planets from "./components/Planets/";
import Title from "./components/Title";
import Wrapper from "./components/Wrapper";
import data from "./planets.json";

class Functionality extends Component {
    // set initial state
    state = {
        data,
        score: 0,
        totalScore: 0
    }

    componentDidMount() {

    }

    correctGuess = (addData) => {
        let { score, totalScore } = this.state;
        let newScore = score + 1;
        let newTotalScore = Math.max(newScore, totalScore);

        this.setState ({
            data: this.randomize(addData),
            score: newScore,
            totalScore: newTotalScore
        });

    }

    randomize = (data) => {
        for (let i = data.length -1; i > 0; i--) {
            let j = Math.floor(Math.random() * i + 1);
            let tempArr = data[i];
            data[i] = data[j];
            data[j] = tempArr;
        } 
        return data;
    }

    state = {
        planet
    };

    render() {
        return (
            <Wrapper className="wrapper">
                <Title>Memory Game</Title>
                const 
                {this.state.data.map(planetobj => (

                    <Planets
                        id={planetobj.id}
                        key={planetobj.id}
                        name={planetobj.name}
                        image={planetobj.image}
                    />

                ))}
            </Wrapper>
        );
    }


}

export default Functionality;
